func TestVerifyBlockData(t *testing.T) {
	testCases := []struct {
		name      string
		blockData cardano.BlockData
		expPass   bool
	}{
		{
			"Success: BlockData",
			cardano.BlockData{
				Height: &cardano.Height{
					RevisionNumber: 0,
					RevisionHeight: 303388,
				},
				Slot:       1214030,
				Hash:       "17e149f64bcdb3c02cfaf474bda7b72c101c3d8f0ef63d98e8ab1bb1426fdef6",
				PrevHash:   "40b933e31ffbb08a719d6166bb076cdf1558f3ff6130c02acd6fa15f359a7bd5",
				EpochNo:    2,
				HeaderCbor: "",
				BodyCbor:   "",
				EpochNonce: "05B05B22EDD9CE5A1868BF7BAF80934AB56E3D9305F385A5F68CB41848A721B1",
				Timestamp:  1707122694,
				ChainId:    "test-chain",
			},
			true,
		},
		{
			"Failed: BlockData wrong BodyCbor",
			cardano.BlockData{
				Height: &cardano.Height{
					RevisionNumber: 0,
					RevisionHeight: 303388,
				},
				Slot:       1214030,
				Hash:       "17e149f64bcdb3c02cfaf474bda7b72c101c3d8f0ef63d98e8ab1bb1426fdef6",
				PrevHash:   "40b933e31ffbb08a719d6166bb076cdf1558f3ff6130c02acd6fa15f359a7bd5",
				EpochNo:    3,
				HeaderCbor: "",
				BodyCbor:   "80",
				EpochNonce: "05B05B22EDD9CE5A1868BF7BAF80934AB56E3D9305F385A5F68CB41848A721B1",
				Timestamp:  1707122694,
				ChainId:    "test-chain",
			},
			false,
		},
	}

	for _, tc := range testCases {

		t.Run(tc.name, func(t *testing.T) {
			vOutput := cardano.VerifyBlock(cardano.BlockHexCbor{
				HeaderCbor:    tc.blockData.HeaderCbor,
				Eta0:          tc.blockData.EpochNonce,
				Spk:           129600,
				BlockBodyCbor: tc.blockData.BodyCbor,
			})
			vOutputObj := cardano.UnpackVerifyBlockOutput(vOutput)
			require.Equal(t, tc.expPass, vOutputObj.IsValid)
		})
	}
}

func TestExtractBlockDataOutput(t *testing.T) {
	testCases := []struct {
		name            string
		blockDataString string
		utxoCount       int
	}{
		{
			"Success: BlockData extract",
			"",
			3,
		},
		// {
		// 	"Failed: BlockData wrong BodyCbor", // TODO: add case failed
		// 	"",
		// 	0,
		// },
	}

	for _, tc := range testCases {

		t.Run(tc.name, func(t *testing.T) {
			vOutputObj := cardano.ExtractBlockDataOutput(cardano.ExtractBlockData(tc.blockDataString))
			require.Equal(t, tc.utxoCount, len(vOutputObj.Outputs))
		})
	}
}
